-- Deploy ggircs:swrs/parameters/table/fuel_mapping to pg
-- requires: swrs/parameters/table/fuel_carbon_tax_detail

begin;

create table ggircs_parameters.fuel_mapping (
  id integer generated always as identity primary key,
  fuel_type varchar(1000),
  fuel_carbon_tax_detail_id integer references ggircs_parameters.fuel_carbon_tax_detail(id)
);

create index ggircs_parameters_fuel_mapping_ct_detail_foreign_key on ggircs_parameters.fuel_mapping(fuel_carbon_tax_detail_id);
create unique index ggircs_parameters_fuel_mapping_fuel_type on ggircs_parameters.fuel_mapping(fuel_type);

comment on table  ggircs_parameters.fuel_mapping is 'The fuel mapping table that maps all reported fuel types from swrs to a normalized fuel type with carbon tax details';
comment on column ggircs_parameters.fuel_mapping.id is 'The internal primary key for the mapping';
comment on column ggircs_parameters.fuel_mapping.fuel_type is 'The type of fuel (from GHGR), Foreign key to fuel';
comment on column ggircs_parameters.fuel_mapping.fuel_carbon_tax_detail_id is 'The foreign key to ggircs_parameters.fuel_carbon_tax_detail';



commit;
